class Heap{constructor(){this.length=0}add(n){if(this.length){if(this.m>n){let t=n;n=this.m;this.m=t}this.r.length>this.l.length?this.l.add(n):this.r.add(n);this.length++}else this.length=1,this.m=n,this.r=new Heap,this.l=new Heap}popmin(){if(this.length){let n=this.m;return this.m=!this.l.length||this.r.length&&this.r.m<this.l.m?this.r.popmin():this.l.popmin(),this.length--,this.length||(delete this.m,delete this.l,delete this.r),n}return}}
