class SegTree{constructor(n,t,i){for(this.t=[n],this.f=t||Math.min,this.e=t?i:Infinity,this.l=n.length;n.length>1;){let t=[];for(let i=0;i<n.length-1;i+=2)t.push(this.f(n[i],n[i+1]));this.t.push(t);n=t}this.h=this.t.length}update(n,t){this.t[0][n]=t;for(let t=1;t<this.h;t++){if(n=Math.floor(n/2),n>=this.t[t].length)break;let i=this.f(this.t[t-1][n*2],this.t[t-1][n*2+1]);if(this.t[t][n]==i)break;else this.t[t][n]=i}}get(n,t){n=Math.max(n,0);t=Math.min(t,this.l);let r=this.e,i=0;while(n+2**i<t){while(n/2**i%1==0&&n+2**i<=t)i++;i--;r=this.f(r,this.t[i][n/2**i]);n+=2**i}while(n<t){while(n+2**i>t)i--;r=this.f(r,this.t[i][n/2**i]);n+=2**i}return r}}
